import timefrom locators.campaign.campaign_mass_duplicate_form_locators import CampaignMassDuplicateFormLocatorsfrom pages.base_page import BasePageclass DspDashboardCampaignsMassDuplicate(BasePage):    def __init__(self, driver):        super().__init__(driver)    def provide_campaign_mass_duplicate_data_and_save(self, campaign_name_list):        self.wait_for_visibility_of_element(CampaignMassDuplicateFormLocators.duplicate_campaign_header_locator)        for iteration in range(len(campaign_name_list)):            self.set_value_into_specific_form_grid_input_field(                CampaignMassDuplicateFormLocators.campaign_mass_duplicate_form_id,                CampaignMassDuplicateFormLocators.name_column, campaign_name_list[iteration], str(iteration + 2))        time.sleep(self.TWO_SEC_DELAY)        self.wait_for_presence_of_element(CampaignMassDuplicateFormLocators.save_button_locator)        self.wait_for_element_to_be_clickable(CampaignMassDuplicateFormLocators.save_button_locator)        time.sleep(self.TWO_SEC_DELAY)        self.click_on_element(CampaignMassDuplicateFormLocators.save_button_locator)