from selenium.webdriver.common.by import Byfrom selenium.webdriver.common.keys import Keysfrom locators.io.invoice_list_locator import InvoiceListLocatorsfrom pages.base_page import BasePageclass DspDashboardInvoiceList(BasePage):    def __init__(self, driver):        super().__init__(driver)        self.driver = driver    def click_on_specific_invoice(self, invoice_title):        locator = (By.XPATH, "//div[text()='" + invoice_title + "']/..//following-sibling::td[4]")        self.wait_for_element_to_be_clickable(locator)        self.click_on_element(locator)    def click_on_specific_credit_invoice(self, credit_invoice_number):        locator = (By.XPATH, "//a[normalize-space()='" + credit_invoice_number + "']")        self.click_on_element(locator)    def search_and_action(self, io_title, action="None"):        self.set_value_into_element(InvoiceListLocators.search_field_locator, io_title)        self.wait_for_presence_of_element(InvoiceListLocators.search_field_locator).send_keys(Keys.ENTER)        self.wait_for_visibility_of_element(InvoiceListLocators.three_dot_of_io_xpath.format(io_title),                                            locator_initialization=True)        if action != 'None':            self.click_on_element(InvoiceListLocators.three_dot_of_io_xpath.format(io_title),                                  locator_initialization=True)