from selenium.webdriver.common.keys import Keysfrom locators.io.io_list_locator import IoListLocatorsfrom pages.base_page import BasePageclass DspDashboardIoList(BasePage):    def __init__(self, driver):        super().__init__(driver)        self.driver = driver    def navigate_to_add_io(self):        self.click_on_element(IoListLocators.create_io_button_locator)    def search_and_action(self, io_title, action="None"):        self.set_value_into_element(IoListLocators.search_field_locator, io_title)        self.wait_for_presence_of_element(IoListLocators.search_field_locator).send_keys(Keys.ENTER)        self.wait_for_visibility_of_element(IoListLocators.three_dot_of_io_xpath.format(io_title),                                            locator_initialization=True)        if action != 'None':            self.click_on_element(IoListLocators.three_dot_of_io_xpath.format(io_title),                                  locator_initialization=True)        if action.lower() == 'edit io':            self.click_on_three_dot_option(IoListLocators.edit_io_label,                                           IoListLocators.dropdown_menu_mx_auto_dropdown_menu_left_class)        if action.lower() == 'invoice':            self.click_on_three_dot_option(IoListLocators.invoice_label,                                           IoListLocators.dropdown_menu_mx_auto_dropdown_menu_left_class)        if action.lower() == 'add proforma':            self.click_on_three_dot_option(IoListLocators.add_proforma_label,                                           IoListLocators.dropdown_menu_mx_auto_dropdown_menu_left_class)